import React, { useState } from "react";

function App(){
    const [count, setCount] = useState(0);

    function increase(){
        setCount(count + 1);
    }

    return (
        <div className="container">
            <h1>{count}</h1>
            <button onClick={increase()}>+</button>
        </div>
    )
}

export default App;
