import React from "react";

function App() {
    const [headingText, setHeadingText] = useState("Hello");
    const [buttonColour, setButtonColour] = useState("white");

    function handleClick(){
        setHeadingText("Submitted");
    }

    function handleMouseOver(){
        setButtonColour("black");
    }

    function handleMouseOut(){
        setButtonColour("white");
    }

    return (
        <div className="container">
        <h1>{headingText}</h1>
        <input type="text" placeholder="What's your name?" />
        <button onClick={handleClick} onMouseOver={handleMouseOver} onMouseOut={handleMouseOut} style={{backgroundColor: buttonColour}}>Submit</button>
        </div>
    );
}

export default App;
