import React from "react";

function InputArea(props) {
  return (
    <div className="form">
        <input 
            onChange={() => {props.changeFunction();}} 
            type="text"
            value={props.input}
        />
      <button onClick={() => {props.addFunction();}}>
        <span>Add</span>
      </button>
    </div>
  );
}

export default InputArea;
