import React, { useState } from "react";

function CreateArea(props) {
    const [newNote, setNewNote] = useState({
        title: "",
        content: "",
    });

    function handleInput(event){
        const {name, value} = event.target;
        setNewNote((prevNote) => {
            return {
                ...prevNote,
                [name]: value,
            }
        });
    }

    return (
        <div>
        <form>
            <input name="title" value={newNote.title} placeholder="Title" onChange={handleInput} />
            <textarea name="content" value={newNote.content} placeholder="Take a note..." onChange={handleInput} rows="3" />
            <button onClick={() => {props.addNote(newNote)}}>Add</button>
        </form>
        </div>
    );
}

export default CreateArea;
